let chosenOption,score=0;const options=document.querySelectorAll(".option"),scoreNumber=document.getElementById("score");options.forEach(e=>{e.addEventListener("click",e=>{e.preventDefault(),chosenOption=e.target.id,showOption()})});const showOption=()=>{options.forEach(e=>{e.style.display="none"});const e=document.createElement("div");document.querySelector(".option-container").prepend(e),e.classList.add("chosen");const o=document.createElement("p");e.appendChild(o),o.innerHTML=`You have chosen ${chosenOption}`};scoreNumber.innerHTML=score;